import React from "react";
import "./about.css";
import AboutImg from "../../assets/about.jpg";
import CV from "../../assets/John-Cv.pdf";
import Info from "./info";

const About = () =>{
    return(
        <section className="about section" id="about">
            <h2 className="section__title">About me</h2>
            <span className="section__subtitle">My introduce</span>
            <div className="about__container container grid">
                <img src={AboutImg} alt="" className="about__img" />
                <div className="about__data">
                    <Info />
                    <p className="about__description">
                        Là sinh viên năm 3 chuyên ngành Kỹ thuật phần mềm Của trường ĐH KHoa học  Tự nhiên, ĐHQG-HCM. 
                        Có tinh thần học hỏi,khám phá, sáng tạo, năng nổ. 
                    </p>
                    <a href={VB} className="button button-flex"></a>
                </div>
            
            
            </div>
        </section>
    )
}
export default About;
